import { formRed, Field } from 'formred';

const FormField = ({ field, handlers, extra: { touched, error } }) => (


        {touched && error && Field error: {error}}

);

class MyForm extends Component {
    render() {
        let {
            formActions: { submit }
            , form: { error, submitting }
        } = this.props;

        return (

                {error && Form error occured: {error}}

                 submit(ev)}>




                    {submitting && Loading...}


        );
    }
};

const formOpts = {
    name: 'myForm'
    , values: {
